import './App.css';

function App() {

  const calculateResult = (event) => {
event.preventDefault();
    console.log("hej");
  };

  return (
    <div className="App">
         <form className="form">
        <legend className="form__legend">Currency converter</legend>
        <p>
            <input className="form__input" type="number" required min="1" step="any"/>
            PLN to
            <select className="form__select" name="currencies" required>
                <option value="">Select currencies</option>
                <option value="EUR">EUR</option>
                <option value="USD">USD</option>
            </select>
        </p>
        <p>
            <button 
              onClick={calculateResult}
              className="form__button">Convert</button>
            <button type="reset">Reset form</button>
        </p>
        <p>You have <span className="form__result"></span></p>
    </form>
    </div>
  );
}

export default App;
