import { useState } from 'react';
import './index.css';

function App() {
  const [amount, setAmount] = useState("");
  const [currency, setCurrency] = useState("");
  const [result, setResult] = useState("");

  const EUR = 4.3;
  const USD = 5;

  const calculateResult = (event) => {
    event.preventDefault();

    let calculatedResult;

    switch (currency) {
      case "EUR":
        calculatedResult = amount / EUR;
        break;
      case "USD":
        calculatedResult = amount / USD;
        break;
      default:
        alert("Nieznana waluta");
        return;
    }

    setResult(`${amount} PLN = ${calculatedResult.toFixed(2)} ${currency}`);
  };

  const handleAmountChange = (event) => {
    setAmount(event.target.value);
  };

  const handleCurrencyChange = (event) => {
    setCurrency(event.target.value);
  };

  return (
    <div className="App">
      <div className="container">
        <form className="form" onSubmit={calculateResult}>
          <legend className="form__legend">Currency converter</legend>
          <p>
            <input 
              className="form__input" 
              type="number" 
              required 
              min="1" 
              step="any"
              value={amount}
              onChange={handleAmountChange}
            />
            PLN to
            <select 
              className="form__select" 
              name="currencies" 
              required
              value={currency}
              onChange={handleCurrencyChange}
            >
              <option value="">Select currencies</option>
              <option value="EUR">EUR</option>
              <option value="USD">USD</option>
            </select>
          </p>
          <p>
            <button type="submit" className="form__button">Convert</button>
            <button 
              type="reset" 
              onClick={() => {
                setAmount("");
                setCurrency("");
                setResult(""); 
              }}
            >
              Reset form
            </button>
          </p>
          <p>You have <span className="form__result">{result}</span></p>
        </form>
      </div>
    </div>
  );
}

export default App;
